mapper.select:
SELECT
FROM project_transfer a
WHERE a.id = #{id}

SELECT

FROM
project_transfer a


    a.del_flag = #{DEL_FLAG_NORMAL}
                             and a.project_id = #{projectId}
                             and a.project_name like concat('%', #{projectName}, '%')
                             and a.from_group_id = #{fromGroupId}
                                 and a.from_create_name like concat('%', #{fromCreateName}, '%')
                                     and a.to_create_name like concat('%', #{toCreateName}, '%')
                             and a.to_group_id = #{toGroupId}

mapper.update:
update project_transfer
project_id = #{projectId}, project_name = #{projectName}, from_group_id = #{fromGroupId}, from_create_by = #{fromCreateBy}, from_create_name = #{fromCreateName}, deal_date = #{dealDate}, to_create_by = #{toCreateBy}, to_create_name = #{toCreateName}, to_group_id = #{toGroupId}, create_date = #{createDate}, del_flag = #{delFlag},
where id = #{id}
