mapper.select:
SELECT
FROM sys_role a
WHERE a.role_id = #{id}

mapper.update:
update sys_role
role_name = #{roleName}, role_key = #{roleKey}, role_sort = #{roleSort}, data_scope = #{dataScope}, status = #{status}, del_flag = #{delFlag}, create_by = #{createBy}, create_date = #{createDate}, update_by = #{updateBy}, update_date = #{updateDate}, remark = #{remark},
where role_id = #{roleId}
